function PuntoDecimal(num)
{
    var dondeDecimal = 0;
    num = num.toString();

    for(var i = 0; i<num.length; i++)
    {//Ojo, estamos asumiendo que no van a haber puntos en el primer lugar de la cadena. (P.e. ".1234")
     if(num[i] == ".")
     {
        break; //Si halla el punto, se sale
     }
     else
     {
        dondeDecimal++; //Mientras no halle el punto, aumenta
     }
    }

    if(dondeDecimal == num.length) 
    {
        dondeDecimal = 0; //No hubo punto decimal en la cadena
    }
    else 
    {
        //Acá encender el punto con el color indicado
        msg.payload = (NeoMedicionFloor + 16*(numDigitos-dondeDecimal)) + "," + Wheel(col);
        node.send(msg);
        num = Math.floor(num * ((overflowNum/10) / (Math.pow(10, (dondeDecimal-1))))); //Recorre a la izquierda la cifra para hacerla "entero" para la siguiente función
    } 

    msg.dondeDecimal = dondeDecimal;
    return num;

}


