function Wheel( WheelPos)
{
  if(WheelPos == 0) return 200 + "," + 200 + "," + 200;


  if (WheelPos < 56 && WheelPos > 0 )
  {
    return (200 + WheelPos) + "," + 200 + "," + 200;
  }

  else if (WheelPos < 111 && WheelPos > 55 )
  {
    WheelPos -= 55;
    return  200 + "," + 200 + "," + (200 + WheelPos);
  }

  else if (WheelPos < 166 && WheelPos > 110 )
  {
    WheelPos -= 110;
    return 200 + "," + (200 + WheelPos) + "," + 200;
  }
}


